mapper.select:
SELECT * FROM (SELECT ROWNUM R, B.* FROM (SELECT * FROM RECIPEREPLY WHERE MEMBER_ID = #{memberdto.member_id} ORDER BY
CREATE_DATE ASC
CREATE_DATE DESC
)B) WHERE R BETWEEN #{pager.startRow} AND #{pager.lastRow}

SELECT * FROM (SELECT ROWNUM R, B.* FROM (SELECT * FROM RECIPEREVIEW WHERE MEMBER_ID = #{memberdto.member_id} ORDER BY
CREATE_DATE ASC
CREATE_DATE DESC
)B) WHERE R BETWEEN #{pager.startRow} AND #{pager.lastRow}

SELECT * FROM (SELECT ROWNUM R, B.* FROM (SELECT * FROM RECIPE WHERE MEMBER_ID = #{memberdto.member_id} ORDER BY
CREATE_DATE ASC
CREATE_DATE DESC
)B) WHERE R BETWEEN #{pager.startRow} AND #{pager.lastRow}

SELECT 


			COUNT(MEMBER_NICKNAME) FROM MEMBER
			WHERE MEMBER_NICKNAME = #{member_nickname}


			COUNT(MEMBER_PHONE) FROM MEMBER
			WHERE MEMBER_PHONE = #{member_phone}


			COUNT(MEMBER_ID) FROM MEMBER
			WHERE MEMBER_ID = #{member_id}

SELECT 
COUNT(MEMBER_ID) FROM MEMBER
WHERE MEMBER_ID = #{member_id}
